mapper.select:
select goods_id, goods_title, goods_cover, goods_status, goods_content, update_user, u.username updateUserName, g.create_time, g.update_time, g.category_id, gc.category_name from goods_master g left join user u on g.update_user = u.uid left join goods_category gc on g.category_id = gc.category_id where 1=1
and goods_title=#{title}
and g.category_id=#{categoryId}
